import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Doughnut } from 'react-chartjs-2';
import { QRCodeSVG } from 'qrcode.react';
import Navbar from '../components/Navbar';
import './EmployeeDashboard.css';

ChartJS.register(ArcElement, Tooltip, Legend);

const EmployeeDashboard = ({ user }) => {
  const navigate = useNavigate();
  const [currentDate, setCurrentDate] = useState(new Date(2026, 0, 19));
  const [filterActive, setFilterActive] = useState('today');
  const [profileImage, setProfileImage] = useState(null);

  useEffect(() => {
    const savedImage = localStorage.getItem('userProfileImage');
    if (savedImage) setProfileImage(savedImage);
  }, []);

  const [showNotifyAbsenceModal, setShowNotifyAbsenceModal] = useState(false);
  const [showRequestCorrectionModal, setShowRequestCorrectionModal] = useState(false);
  const [showReceiptModal, setShowReceiptModal] = useState(false);

  const [absenceData, setAbsenceData] = useState({
    date: '',
    reason: '',
    proof: null
  });

  const [correctionData, setCorrectionData] = useState({
    date: '',
    type: 'time-in',
    correctTime: '',
    reason: ''
  });

  const handleFilterByDate = (period) => {
    const today = new Date(2026, 0, 19);
    setFilterActive(period);

    if (period === 'today') setCurrentDate(today);
    if (period === 'lastweek') {
      const d = new Date(today);
      d.setDate(d.getDate() - 7);
      setCurrentDate(d);
    }
    if (period === 'lastmonth') {
      const d = new Date(today);
      d.setMonth(d.getMonth() - 1);
      setCurrentDate(d);
    }
  };

  const formatDate = (date) =>
    date.toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });

  const handleLogout = () => navigate('/login');
  const handleClaim = () => alert('Payment claim confirmed.');
  const handleViewReceipt = () => setShowReceiptModal(true);

  return (
    <>
    <div className="employee-container">
      <Navbar user={user} onLogout={handleLogout} />

      <div className="welcome">
        <h1>Welcome, {user?.employeeName || 'Employee'}</h1>
      </div>

      {/* Employee Profile */}
      <div className="employee-profile-card">
        <h2>Employee Profile</h2>

        <div className="profile-content">
          <div className="profile-left">
            <div className="avatar-placeholder">
              {profileImage ? (
                <img src={profileImage} alt="Profile" />
              ) : (
                user?.employeeName?.substring(0, 2).toUpperCase() || 'EM'
              )}
            </div>
            <button onClick={() => navigate('/profile')}>Edit Profile</button>
          </div>

          <div className="profile-middle">
            <p><strong>Name:</strong> {user?.employeeName}</p>
            <p><strong>ID:</strong> EMP001</p>
            <p><strong>Position:</strong> Truck Driver</p>
          </div>

          <div className="profile-right">
            <QRCodeSVG
              value={`EMP001-${user?.employeeName}`}
              size={140}
            />
          </div>
        </div>
      </div>

      {/* Filter */}
      <div className="filter-section">
        <button onClick={() => handleFilterByDate('today')}>Today</button>
        <button onClick={() => handleFilterByDate('lastweek')}>Last Week</button>
        <button onClick={() => handleFilterByDate('lastmonth')}>Last Month</button>
        <span>{formatDate(currentDate)}</span>
      </div>

      {/* Dashboard */}
      <div className="dashboard">
        <div className="report-card">
          <h2>My Attendance Report</h2>
        </div>

        <div className="report-card">
          <h2>My Salary Report</h2>
        </div>
      </div>

      {/* ✅ FIXED SECTION — Salary Actions */}
      <div className="salary-action-section">
        <div className="salary-status-row">
          <span className="status-badge pending">Pending</span>
          <span className="status-badge released">Released</span>
          <span className="status-badge claimed">Claimed</span>
        </div>

        <div className="salary-action-row">
          <span>Release Date: 10/10/2025</span>
          <button onClick={handleClaim}>Claim</button>
          <button onClick={handleViewReceipt}>View Receipt</button>
        </div>
      </div>

      {/* Receipt Modal */}
      {showReceiptModal && (
        <div className="modal-overlay" onClick={() => setShowReceiptModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h2>Salary Receipt</h2>
            <p>₱ 46,000</p>
            <button onClick={() => setShowReceiptModal(false)}>Close</button>
          </div>
        </div>
      )}
    </div>
    </>
  );
};

export default EmployeeDashboard;
