import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Doughnut } from 'react-chartjs-2';
import { QRCodeSVG } from 'qrcode.react';
import Navbar from '../components/Navbar';
import './EmployeeDashboard.css';

ChartJS.register(ArcElement, Tooltip, Legend);

const EmployeeDashboard = ({ user }) => {
  const navigate = useNavigate();

  const [profileImage, setProfileImage] = useState(null);
  const [filterActive, setFilterActive] = useState('today');

  const [showNotifyAbsenceModal, setShowNotifyAbsenceModal] = useState(false);
  const [showRequestCorrectionModal, setShowRequestCorrectionModal] = useState(false);
  const [showReceiptModal, setShowReceiptModal] = useState(false);

  const [absenceData, setAbsenceData] = useState({
    date: '',
    reason: '',
    proof: null
  });

  const [correctionData, setCorrectionData] = useState({
    date: '',
    type: 'time-in',
    correctTime: '',
    reason: ''
  });

  useEffect(() => {
    const savedImage = localStorage.getItem('userProfileImage');
    if (savedImage) setProfileImage(savedImage);
  }, []);

  const handleLogout = () => navigate('/login');

  /* ===================== ABSENCE ===================== */

  const handleSubmitAbsence = () => {
    if (!absenceData.date || !absenceData.reason) {
      alert('Please fill in all required fields');
      return;
    }

    console.log('Absence Submitted:', absenceData);

    alert(
      absenceData.proof
        ? `Absence submitted with file: ${absenceData.proof.name}`
        : 'Absence submitted without proof'
    );

    setShowNotifyAbsenceModal(false);
    setAbsenceData({ date: '', reason: '', proof: null });
  };

  /* ===================== CORRECTION ===================== */

  const handleSubmitCorrection = () => {
    if (!correctionData.date || !correctionData.correctTime || !correctionData.reason) {
      alert('Please fill in all required fields');
      return;
    }

    alert('Correction request sent');
    setShowRequestCorrectionModal(false);
    setCorrectionData({ date: '', type: 'time-in', correctTime: '', reason: '' });
  };

  return (
    <>
      <div className="employee-container">
        <Navbar user={user} onLogout={handleLogout} />

        <h1 className="welcome">Welcome, {user?.employeeName || 'Employee'}</h1>

        {/* ===================== PROFILE ===================== */}
        <div className="employee-profile-card">
          <h2>Employee Profile</h2>

          <div className="profile-content">
            <div className="profile-left">
              <div className="avatar-placeholder">
                {profileImage ? (
                  <img src={profileImage} alt="Profile" />
                ) : (
                  user?.employeeName?.substring(0, 2).toUpperCase() || 'EM'
                )}
              </div>
              <button onClick={() => navigate('/profile')}>Edit Profile</button>
            </div>

            <div className="profile-middle">
              <p><strong>Name:</strong> {user?.employeeName}</p>
              <p><strong>Employee ID:</strong> EMP001</p>
              <p><strong>Position:</strong> Truck Driver</p>
            </div>

            <div className="profile-right">
              <QRCodeSVG
                value={`EMP001-${user?.employeeName}`}
                size={140}
              />
            </div>
          </div>
        </div>

        {/* ===================== DASHBOARD ===================== */}
        <div className="dashboard">
          <button className="btn-notify" onClick={() => setShowNotifyAbsenceModal(true)}>
            Notify Absence
          </button>

          <button className="btn-notify" onClick={() => setShowRequestCorrectionModal(true)}>
            Request Correction
          </button>
        </div>
      </div>

      {/* ===================== NOTIFY ABSENCE MODAL ===================== */}
      {showNotifyAbsenceModal && (
        <div className="modal-overlay" onClick={() => setShowNotifyAbsenceModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h2>Notify Absence</h2>

            <div className="form-group">
              <label>Date *</label>
              <input
                type="date"
                value={absenceData.date}
                onChange={(e) =>
                  setAbsenceData({ ...absenceData, date: e.target.value })
                }
              />
            </div>

            <div className="form-group">
              <label>Reason *</label>
              <textarea
                rows="4"
                value={absenceData.reason}
                onChange={(e) =>
                  setAbsenceData({ ...absenceData, reason: e.target.value })
                }
              />
            </div>

            <div className="form-group">
              <label>Attach Proof (Optional)</label>
              <input
                type="file"
                accept="image/*,.pdf"
                onChange={(e) => {
                  const file = e.target.files[0];
                  if (!file) return;

                  if (file.size > 5 * 1024 * 1024) {
                    alert('File must be under 5MB');
                    e.target.value = '';
                    return;
                  }

                  setAbsenceData({ ...absenceData, proof: file });
                }}
              />

              {absenceData.proof && (
                <p className="file-name">
                  Selected file: <strong>{absenceData.proof.name}</strong>
                </p>
              )}
            </div>

            <div className="modal-footer">
              <button onClick={() => setShowNotifyAbsenceModal(false)}>Cancel</button>
              <button onClick={handleSubmitAbsence}>Submit</button>
            </div>
          </div>
        </div>
      )}

      {/* ===================== CORRECTION MODAL ===================== */}
      {showRequestCorrectionModal && (
        <div className="modal-overlay" onClick={() => setShowRequestCorrectionModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h2>Request Correction</h2>

            <input
              type="date"
              value={correctionData.date}
              onChange={(e) =>
                setCorrectionData({ ...correctionData, date: e.target.value })
              }
            />

            <select
              value={correctionData.type}
              onChange={(e) =>
                setCorrectionData({ ...correctionData, type: e.target.value })
              }
            >
              <option value="time-in">Time In</option>
              <option value="time-out">Time Out</option>
            </select>

            <input
              type="time"
              value={correctionData.correctTime}
              onChange={(e) =>
                setCorrectionData({ ...correctionData, correctTime: e.target.value })
              }
            />

            <textarea
              rows="4"
              value={correctionData.reason}
              onChange={(e) =>
                setCorrectionData({ ...correctionData, reason: e.target.value })
              }
            />

            <div className="modal-footer">
              <button onClick={() => setShowRequestCorrectionModal(false)}>Cancel</button>
              <button onClick={handleSubmitCorrection}>Submit</button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default EmployeeDashboard;
